        # print(self.listDirSam)
        self.currentSamPage = page
        self.numSamPage = len(self.listDirSam) // self.numItem
        counter = 0

        if len(self.listDirSam) % self.numItem == 0:
            self.numSamPage -= 1
        if len(self.listDirSam) > self.numItem:
            self.prevSam.show()
            self.nextSam.show()
        else:
            self.prevSam.hide()
            self.nextSam.hide()
