    def openFilePath(self):
        dlg = QFileDialog()
        dlg.setFileMode(QFileDialog.Directory)
        if dlg.exec_():
            filenames = dlg.selectedFiles()
        path = filenames[0]
        if path == "" or path is None:
            return
        self.storeDir = path + '/'
        self.currentSub = ""
        self.updateSub()

        self.updateSam(listDir=-1)

